erHeight();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;JS：&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;占位宽： div.offsetWidth&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		div.offsetHeight&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;JQ:     $(&lt;span class=&#34;string&#34;&gt;&amp;quot;div&amp;quot;&lt;/span&gt;).outerWidth();&lt;/span&gt;&lt;br&gt;&lt;span class=&#34;line&#34;&gt;		$(&lt;span class=&#34;string&#34;&gt;&amp;quot;div&amp;quot;&lt;/span&gt;).outerHeight();&lt;/span&gt;&lt;br&gt;&lt;/pre&gt;&lt;/td&gt;&lt;/tr&gt;&lt;/table&gt;&lt;/figure&gt;
